def get_contract_abi(contract_address):
    etherscan_api_key = 'IJY37SRWJ7E6Z6ZR3M3S9B8QSVZCSSE9PB'
    url = f"https://api.etherscan.io/api?module=contract&action=getabi&address={contract_address}&apikey={etherscan_api_key}"

    response = requests.get(url)
    data = response.json()

    if data['status'] == '1':
        return data['result']
    else:
        raise Exception("ABI not found or failed to fetch")


def decode_transaction_input(transac):
    abi = get_contract_abi(transac['hash'])
    contract = w3.eth.contract(abi=abi)
    try:
        func_obj, func_params = contract.decode_function_input(
            transac['input'])
        return {
            "function": func_obj.fn_name,
            "parameters": func_params
        }
    except Exception as e:
        return f"Error decoding input: {e}"


