weth_address = Web3.to_checksum_address(
    '0xC02aaA39b223FE8D0A0e5C4F27eAD9083C756Cc2')
usdt_address = Web3.to_checksum_address(
    '0xdAC17F958D2ee523a2206206994597C13D831ec7')
pool_address = Web3.to_checksum_address(
    '0x0d4a11d5EEaaC28EC3F61d100daF4d40471f1852')
router_address = Web3.to_checksum_address(
    '0x7a250d5630B4cF539739dF2C5dAcb4c659F2488D')
whale_addr = weth_address


def get_funds(web3, token, whale, account):
    native_before = web3.eth.get_balance(account) / 10 ** 18
    token_before = token.functions.balanceOf(account).call() / 10 ** 18

    web3.eth.send_transaction({
        'from': whale,
        'to': account,
        'value': int(10 * 10 ** 18)
    })

    token.functions.transfer(account, int(10 * 10e18)).transact({
        'from': whale_addr,
    })

    native_after = web3.eth.get_balance(account) / 10e18
    token_after = token.functions.balanceOf(account).call() / 10e18

    print('Got funds: account ({}) native/token balance (before/after) = ({:.2f}/{:.2f})/({:.2f}/{:.2f})'.format(
        account,
        native_before,
        native_after,
        token_before,
        token_after
    ))


def get_balance(token, account, raw=False):
    balance = token.functions.balanceOf(account).call()
    if not raw:
        balance /= 10 ** token.functions.decimals().call()

    return balance


def get_v2_pool_price(reserve0, reserve1, decimals0, decimals1):
    amount0 = reserve0 / 10 ** decimals0
    amount1 = reserve1 / 10 ** decimals1

    return amount0 / amount1, amount1 / amount0


rpc = 'http://127.0.0.1:1234'
web3 = Web3(Web3.HTTPProvider(rpc))
priv_key = '0x6cbed15c793ce57650b9877cf6fa156fbef513c4e6134f022a85b1ffdd59b2a1'
addr = Web3.to_checksum_address(web3.eth.account.from_key(priv_key).address)
weth = web3.eth.contract(address=weth_address, abi=erc20_abi)
usdt = web3.eth.contract(address=usdt_address, abi=erc20_abi)
pool = web3.eth.contract(address=pool_address, abi=pool_abi)
router = web3.eth.contract(address=router_address, abi=router_abi)
decimals0 = weth.functions.decimals().call()
decimals1 = usdt.functions.decimals().call()

get_funds(web3, weth, whale_addr, addr)
weth.functions.approve(router_address, 2 ** 256 - 1).transact({'from': addr})

print('usdt balance before swap: {}'.format(get_balance(usdt, addr)))
reserve0, reserve1, _ = pool.functions.getReserves().call()
print('weth/usdt pool price: {}'.format(get_v2_pool_price(reserve0,
      reserve1, decimals0, decimals1)))


router.functions.swapExactTokensForTokens(
    10 ** decimals0,
    0,
    [weth_address, usdt_address],
    addr,
    2 ** 256 - 1
).transact({'from': addr})

print('usdt balance after swap: {}'.format(get_balance(usdt, addr)))
reserve0, reserve1, _ = pool.functions.getReserves().call()
print('weth/usdt pool price: {}'.format(get_v2_pool_price(reserve0,
      reserve1, decimals0, decimals1)))
